imprimir_peliculas :-
    pelicula(Nombre, Sinopsis, Actores, Duracion, Idioma, Anio, Enlaces, Categoria, Costo, Imagen),
    write(Nombre), nl,
    write(Sinopsis), nl,
    write(Actores), nl,
    write(Duracion), nl,
    write(Idioma), nl,
    write(Anio), nl,
    write(Enlaces), nl,
    write(Categoria), nl,
    write(Costo), nl,
    write(Imagen), nl,
    nl,
    fail.

