    pelicula(Nombre, Sinopsis, Actores, Duracion, Idioma, Anio, Enlaces, Categoria, Costo, Imagen),
    Anio >= 2020,
    write(Nombre), nl,
    write(Sinopsis), nl,
    write(Actores), nl,
    write(Duracion), nl,
    write(Idioma), nl,
    write(Anio), nl,
