imprimir_peliculas_recomendadas(Categoria) :-
    imprimir_peliculas_recomendadas(Categoria, []).

imprimir_peliculas_recomendadas(_, PeliculasImpresas) :-
    length(PeliculasImpresas, Length),
    Length >= 3. % Se detiene la ejecucion despues de tres películas.

imprimir_peliculas_recomendadas(Categoria, PeliculasImpresas) :-
    pelicula(Nombre, Sinopsis, Actores, Duracion, Idioma, Anio, Enlaces, Categoria, Costo, Imagen),
    \+ member(pelicula(Nombre, _, _, _, _, _, _, _, _), PeliculasImpresas),
    write(Nombre), nl,
    write(Sinopsis), nl,
    write(Actores), nl,
    write(Duracion), nl,
    write(Idioma), nl,
    write(Anio), nl,
    write(Enlaces), nl,
    write(Categoria), nl,
    write(Costo), nl,
    write(Imagen), nl,
    nl,
    imprimir_peliculas_recomendadas(Categoria, [pelicula(Nombre, _, _, _, _, _, _, _, _) | PeliculasImpresas]).